import { NavLink, Link, useLocation } from 'react-router-dom'
import { Dialog, Transition, Tab, Switch } from '@headlessui/react'
import { useState, Fragment } from 'react'

import Favorite from '../Favorite/Favorite'
// import Popover from '../UI/UiPopover/Popover'
import NavTabs from './NavTabs/NavTabs'
import CATEGORY_DESCRIPTION from '../../constants/categoryDescription'
import { getRouteFromUrl } from '../../services/getData'

import SVGComponentFilms from './img/menuIcons/films'
import SVGComponentCharacters from './img/menuIcons/characters'
import SVGComponentSpecies from './img/menuIcons/species'
import SVGComponentStarships from './img/menuIcons/starships'
import SVGComponentPlanets from './img/menuIcons/planets'
import SVGComponentVehicles from './img/menuIcons/vehicles'


// dublacate
const CATEGORIES_SELECTED = [
  'people',
  'species',
  'films',
  'starships',
  'planets',
  'vehicles',
]

function Modal() {
  const [isOpen, setIsOpen] = useState(false)

  const closeModal = () => {
    setIsOpen(false)
  }

  const openModal = () => {
    setIsOpen(true)
  }

  const location = useLocation()

  const isCategorySelected = () => {
    return CATEGORIES_SELECTED.some(
      (category) => category === getRouteFromUrl(location.pathname)
    )
  }

  return (
    <>
      {isCategorySelected() ? (
        <button
          type="button"
          onClick={openModal}
          className="
      flex
      gap-1
      bg-opacity-20
      hover:bg-opacity-3
      focus:outline-none 
      focus-visible:ring-2
      focus-visible:ring-white 
      focus-visible:ring-opacity-75
      neumorphism-inner-shadow
      rounded-3xl
      bg-gray-dark
      px-8 
      py-3"
        >
          Library
          <div className="pt-1">
            <SVGArrow />
          </div>
        </button>
      ) : (
        <button
          type="button"
          onClick={openModal}
          className="
      flex
      gap-1
      bg-opacity-20
      hover:bg-opacity-3
      focus:outline-none 
      focus-visible:ring-2
      focus-visible:ring-white 
      focus-visible:ring-opacity-75"
        >
          Library
          <div className="pt-1">
            <SVGArrow />
          </div>
        </button>
      )}

      <Transition appear show={isOpen} as={Fragment}>
        <Dialog as="div" className="relative z-10" onClose={closeModal}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-25" />
          </Transition.Child>

          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex justify-center mt-24 p-4 text-center">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel
                  className="
                neumorphism-knob-shadow-inner-drop 
                border-8
              border-white
                w-9/12 
                transform 
                overflow-hidden
                rounded-3xl
                bg-knob
                p-6
                text-left
                align-middle
                transition-all"
                >
                  <div
                    className="
                  neumorphism-knob-elevation-shadow-drop 
                  p-6  
                  rounded-3xl
                  px-12"
                  >
                    <Dialog.Title
                      as="h2"
                      className="
                      pt-6
                      flex
                      justify-center
                      pb-8
                      "
                    >
                      <SVGTitleDialog />
                    </Dialog.Title>
                    <Tabs closeModal={closeModal} />
                  </div>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
    </>
  )
}

const svgComponents = {
  Films: SVGComponentFilms,
  People: SVGComponentCharacters,
  Species: SVGComponentSpecies,
  Starships: SVGComponentStarships,
  Planets: SVGComponentPlanets,
  Vehicles: SVGComponentVehicles,
}

function CustomTab() {
  return (
    <Tab.List className="flex justify-center">
      {Object.keys(CATEGORY_DESCRIPTION).map((category) => (
        <Tab key={category} className="w-full" selected>
          {({ selected }) => {
            const SVGComponent = svgComponents[category]
            const svgProps = {
              selected,
              color: selected ? '#EEBF00' : '#000000',
            }

            return (
              <div>
                <div className="flex mx-auto items-center h-20 w-20">
                  <SVGComponent
                    selected={svgProps.selected}
                    color={svgProps.color}
                  />
                </div>
                <div
                  className="
                  flex 
                  justify-center 
                  items-center 
                  ui-selected:bg-gold
                  ui-selected:shadow-iner
                  bg-black
                  h-1
                  mt-4 
                  mb-2
                "
                >
                  <div
                    className="
                    ui-selected:border-gold
                    ui-selected:border-4
                    border-black 
                    border-2
                    bg-gray 
                    w-5 h-5  
                    rounded-full"
                  />
                </div>
                {category}
              </div>
            )
          }}
        </Tab>
      ))}
    </Tab.List>
  )
}

function Tabs({ closeModal }) {
  return (
    <Tab.Group>
      <CustomTab />
      <Tab.Panels className="mt-2 ">
        {Object.entries(CATEGORY_DESCRIPTION).map(([categoryName, posts]) => (
          <Tab.Panel key={posts[0].id}>
            {posts.map((post) => (
              <div key={post.id}>
                <h3>{post.title}</h3>
                <div>
                  <p>{post.content}</p>
                </div>
                <div className="mt-4">
                  {/* Generate the link from the categoryName */}
                  <Link
                    to={`/${categoryName.toLowerCase()}`}
                    className="inline-flex justify-center rounded-md border border-transparent bg-blue-100 px-4 py-2 text-sm font-medium text-blue-900 hover:bg-blue-200 focus:outline-none focus-visible:ring-2 focus-visible:ring-blue-500 focus-visible:ring-offset-2"
                    onClick={closeModal}
                  >
                    See more!
                  </Link>
                </div>
              </div>
            ))}
          </Tab.Panel>
        ))}
      </Tab.Panels>
    </Tab.Group>
  )
}

export default function Header() {
  return (
    <nav
      className="
    neumorphism-drop-shadow
    container 
    flex
    border-2
    border-white
    rounded-3xl
    bg-header
    px-8
    py-4
    "
    >
      <SVGStarWars />
      <ul className="flex mx-auto gap-6 items-center font-bold">
        <li>
          <NavLink
            to="/"
            className={({ isActive, isPending }) =>
              isPending
                ? 'pending'
                : isActive
                ? 'neumorphism-inner-shadow rounded-3xl bg-gray-dark px-8 py-3'
                : ''
            }
            type="button"
          >
            Home
          </NavLink>
        </li>
        <li>
          <Modal />
        </li>
        <NavTabs />
      </ul>
      <div className="ml-auto flex gap-6">
        {/* <Popover /> */}
        <Favorite />
        {/* <SVGFavorite /> */}
        <Toggle />
      </div>
    </nav>
  )
}

function Toggle() {
  const [enabled, setEnabled] = useState(false)

  return (
    <Switch
      checked={enabled}
      onChange={setEnabled}
      className={`${enabled ? 'bg-gold' : 'bg-teal-700'}
      inner-shadow
      relative
      h-[38px] 
      w-[74px] 
      shrink-0 
      cursor-pointer 
      rounded-2xl

      transition-colors 
      duration-200 
      ease-in-out 
      focus:outline-none 
      focus-visible:ring-2  
      focus-visible:ring-white 
      focus-visible:ring-opacity-75
      flex items-center
      `}
    >
      <span
        aria-hidden="true"
        className={`${enabled ? 'translate-x-10' : 'translate-x-1'}
        neumorphism-drop-shadow
        pointer-events-none 
        inline-block
        h-[30px] 
        w-[30px] 
        transform 
        rounded-full 
        bg-white 
        ring-0 
        transition 
        duration-200 
        ease-in-out
        `}
      >
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
          <SVGMoon />
        </div>
      </span>
    </Switch>
  )
}

